<?php\n// Mail Server Configuration\n$mailbox = '{host:port/imap}INBOX'; // Replace host and port appropriately\n$username = 'your_username';\n$password = 'your_password';\n\n// Connect to the IMAP mailbox\n$connection = imap_open($mailbox, $username, $password);\n\nif (!$connection) {\n    die('Failed to connect to mail server: ' . imap_last_error());\n}\n\n// Get the total number of emails in the INBOX\n$emailCount = imap_num_msg($connection);\n\nif ($emailCount > 0) {\n    // Fetch the latest email (most recent)\n    $overview = imap_fetch_overview($connection, $emailCount, FT_UID);\n    $messageNumber = $overview[0]->msgno;\n\n    // Get the body of the email\n    $body = imap_body($connection, $messageNumber);\n\n    echo "Body of the latest email: " .htmlspecialchars($body);\n} else {\n    echo 'No emails found in the inbox.';\n}\n\n// Close the IMAP connection\nimap_close($connection);\n?>