amespace App\Controller;\n\nuse Symfony\Bundle\FrameworkBundle\Controller\AbstractController;\nuse Symfony\Component\Routing\Generator\UrlGeneratorInterface;\nuse Symfony\Component\HttpFoundation\Response;\n\nclass ProductController extends AbstractController\n{\n    public function show($slug): Response\n    {\n        // Assuming $product is fetched from a repository based on the slug\n        $product = 'Product details for: ' . htmlspecialchars($slug);\n\n        return new Response('<html><body>' . $product . '</body></html>');\n    }\n\n    public function index(UrlGeneratorInterface $urlGenerator): Response\n    {\n        $url = $urlGenerator->generate('app_product_show', [\n            'slug' => 'example-product',\n        ]);\n\n        return new Response('Check out this URL: <a href="' . htmlspecialchars($url) . '">' . htmlspecialchars($url) . '</a>');\n    }\n}