class SessionController extends Zend_Controller_Action\n{\n    protected $session;\n\n    public function init()\n    {\n        $this->_helper->viewRenderer->setNoRender();\n        $this->_helper->getHelper('layout')->disableLayout();\n        $this->session = new Zend_Session_Namespace('your_namespace');\n    }\n\n    public function printthingAction()\n    {\n        $textme = $this->getRequest()->getParam('textme', '');\n        if (!isset($this->session->messages)) {\n            $this->session->messages = array();\n        }\n        $this->session->messages[] = $textme;\n\n        echo $this->view->tabbedbox(implode("\n", $this->session->messages));\n    }\n}