<?php\n\n// Define your credentials\n$username = 'your_username';\n$password = 'your_password';\n$url = 'http://subdomaina.example.com/protected_file.xml';\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set the URL of the XML file\ncurl_setopt($ch, CURLOPT_URL, $url);\n\n// Enable HTTP Basic Authentication\ncurl_setopt($ch, CURLOPT_HTTPAUTH, CURLAUTH_BASIC);\ncurl_setopt($ch, CURLOPT_USERPWD, "$username:$password");\n\n// Return transfer as a string\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n// Execute cURL session and store the data from XML file\n$data = curl_exec($ch);\n\n// Check for errors in the cURL execution\nif (curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n} else {\n    // Load the XML data into a SimpleXMLElement object\n    $xml = simplexml_load_string($data);\n    \n    if ($xml === false) {\n        echo "Failed loading XML";\n    } else {\n        // Use your XML data here, for example:\n        print_r($xml);\n    }\n}\n\n// Close cURL session\ncurl_close($ch);\n\n?>