function simpleCategorizePost($text) {\n    $lowercaseText = strtolower($text);\n    \n    // Define keywords for each category\n    $buyKeywords = ['buy', 'looking for', 'need'];\n    $sellKeywords = ['sell', 'selling', 'get rid of'];\n    $items = ['house', 'car', 'laptop'];\n\n    $category = '';\n    $item = '';\n\n    foreach ($buyKeywords as $keyword) {\n        if (strpos($lowercaseText, $keyword) !== false) {\n            $category = 'buy';\n            break;\n        }\n    }\n\n    foreach ($sellKeywords as $keyword) {\n        if (strpos($lowercaseText, $keyword) !== false) {\n            $category = 'sell';\n            break;\n        }\n    }\n\n    // Detect item\n    foreach ($items as $itemKeyword) {\n        if (strpos($lowercaseText, $itemKeyword) !== false) {\n            $item = $itemKeyword;\n            break;\n        }\n    }\n\n    return [$category, $item];\n}\n\n$post = "I'm looking for a house";\nlist($category, $item) = simpleCategorizePost($post);\necho "Category: " . $category . ", Item: " . $item;