// Example data structure (assuming $array1 and $array2 are populated with data)\n$array1 = [\n    1 => ['id' => 1, 'title' => 'Question Title 1'],\n    2 => ['id' => 2, 'title' => 'Question Title 2'],\n];\n\n$array2 = [\n    1 => ['id' => 1, 'tags' => ['tag1', 'tag2']],\n    2 => ['id' => 2, 'tags' => ['tag3']],\n];\n\n// Loop over the question IDs\nforeach ($array1 as $question_id => $question_data) {\n    // Print the title from array1\n    echo "Title: " . htmlspecialchars($question_data['title']) . "\n";\n\n    // Check if tags exist for this question in array2\n    if (isset($array2[$question_id]['tags'])) {\n        // Print all tags for this question\n        echo "Tags: ";\n        foreach ($array2[$question_id]['tags'] as $tag) {\n            echo htmlspecialchars($tag) . ", ";\n        }\n        echo "\n";\n    } else {\n        echo "No tags available.\n";\n    }\n}