use PhpOffice\PhpSpreadsheet\Spreadsheet;\nuse PhpOffice\PhpSpreadsheet\Style\NumberFormat;\n\nclass TestExcel extends Spreadsheet {\n\n    private $number;\n\n    public function __construct($number){\n        parent::__construct();\n\n        // Set the number\n        $this->number = $number;\n\n        // Create a new sheet and set the value\n        $sheet = $this->getActiveSheet();\n        $cell = 'A1';\n        $sheet->setCellValue($cell, $this->number->getNumber());\n\n        // Set the number format to avoid scientific notation\n        $sheet->getStyle($cell)->getNumberFormat()->setFormatCode(NumberFormat::FORMAT_NUMBER);\n    }\n}