$ldap = ldap_connect("ldap://DC=company,DC=local");\n   if (!$ldap) {\n       die('Unable to connect to server');\n   }\n   ldap_set_option($ldap, LDAP_OPT_REFERRALS, 0);\n   \n   // Use Windows Authentication with Kerberos/NTLM\n   ldap_sasl_bind($ldap); // Note: This binds with the user credentials supplied by IIS.\n   \n   $result = ldap_search($ldap, "OU=Groups,OU=Division,DC=company,DC=local", "(CN=GroupName)");\n   if (!$result) {\n       die('Search failed');\n   }\n   \n   $entries = ldap_get_entries($ldap, $result);\n   // Process entries\n   \n   ldap_close($ldap);