<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $input = trim($_POST['project-date']);\n\n    // Define possible formats with default values for missing parts\n    $formats = [\n        'Y/m' => '%s-01 00:00:01',\n        'Y/m/d' => '%s 00:00:01',\n        'Y/m/d/H' => '%s:%i:01',\n        'Y/m/d/H/i' => '%s:%i:s',\n        'Y/m/d/H/i/s' => '%s'\n    ];\n\n    $date = null;\n    foreach ($formats as $format => $pattern) {\n        $dt = DateTime::createFromFormat($format, $input);\n        if (!$dt || DateTime::getLastErrors()['warning_count'] > 0 || DateTime::getLastErrors()['error_count'] > 0) {\n            continue; // Try next format\n        }\n        $timestamp = $dt->getTimestamp();\n        $date = sprintf('%s', date(str_replace('%', '', $pattern), $timestamp));\n        break;\n    }\n\n    if (!$date) {\n        echo "Invalid date format. Please use YYYY/MM, YYYY/MM/DD, or YYYY/MM/DD/H/I/S.";\n    } else {\n        // Insert into SQLite database\n        // Assuming a PDO connection: $pdo\n        try {\n            $stmt = $pdo->prepare("INSERT INTO projects (completion_date) VALUES (:date)");\n            if ($stmt->execute([':date' => $date])) {\n                echo "Project date saved successfully.";\n            } else {\n                echo "Failed to save project date.";\n            }\n        } catch(PDOException $e) {\n            echo "ERROR: Could not execute sql: ".$sql. ". " .$e->getMessage();\n        }\n    }\n}\n?>