// model.php\n$screen = "list1.inc";            // Set the initial screen value\n\nrequire "controller.php";         // This should include initSession\n\nfunction initSession() {\n    global $screen;                   // Declare as global to access it inside this function\n    echo $screen . "\n";              // Output current value of screen\n}\n\ninitSession();                    // Call the function to see initial setting\n\n$screen = "delete1.inc";          // Change the screen value\nrequire_once "controller.php";     // Use include_once to ensure file is included only once\n\ninitSession();                    // Check if the value is updated