function generateUniqueKey($length = 7) {\n    // Generate random bytes\n    $bytes = random_bytes(ceil($length / 2));\n    \n    // Convert bytes to hexadecimal representation\n    $key = substr(bin2hex($bytes), 0, $length);\n    \n    // Remove characters L and O if necessary\n    $key = str_replace(['l', 'L', 'o', 'O'], '', $key);\n    \n    // Ensure the key is the desired length by padding or trimming\n    while (strlen($key) < $length) {\n        $bytes = random_bytes(ceil(($length - strlen($key)) / 2));\n        $key .= substr(bin2hex($bytes), 0, ($length - strlen($key)));\n    }\n    \n    return strtolower(substr($key, 0, $length));\n}\n\n// Example usage\necho generateUniqueKey();