// In your controller action\npublic function updateProfileAction()\n{\n    // Disable layout and view renderer (if not already)\n    $this->_helper->layout()->disableLayout();\n    $this->_helper->viewRenderer->setNoRender(true);\n\n    // Assume you have a form class defined as \Application\Form\EditProfileForm\n    $form = new \Application\Form\EditProfileForm();\n\n    // Retrieve POST data\n    $data = $this->_request->getPost();\n\n    if ($form->isValid($data)) {\n        // Process the valid data (e.g., save to database)\n        // ...\n\n        // Return success response as JSON\n        echo json_encode(['success' => true]);\n    } else {\n        // Return errors as JSON\n        echo json_encode([\n            'success' => false,\n            'errors' => $form->getMessages()\n        ]);\n    }\n}