function formatNumber($number) {\n    $units = ['', 'K', 'Mil', 'Bil', 'Tr'];\n    for ($i = 0; $number >= 1000 && $i < count($units) - 1; $i++) {\n        $number /= 1000;\n    }\n    return round($number, 1) . ' ' . $units[$i];\n}\n\n// Example usage:\necho formatNumber(100500000); // Outputs: 100.5 Mil\necho formatNumber(200400);    // Outputs: 200.4 K\necho formatNumber(143000000); // Outputs: 143 Mil\necho formatNumber(52000000000);// Outputs: 52 Bil