require 'vendor/autoload.php';\n\nuse GuzzleHttp\Psr7\Uri;\nuse GuzzleHttp\Psr7\UriResolver;\n\n$sourceUrl = '/blah.html';\n$baseHost = 'http://www.example.com';\n\n$baseUrl = new Uri($baseHost);\n$url = new Uri($sourceUrl);\n\n// Resolve the absolute URL\n$resolvedUrl = UriResolver::resolve($baseUrl, $url);\n\necho (string)$resolvedUrl;  // Outputs: http://www.example.com/blah.html