class Payment {\n    public $type;\n    public $amount;\n    public $account;\n\n    public function __construct($type, $amount, $account) {\n        $this->type = $type;\n        $this->amount = $amount;\n        $this->account = $account;\n    }\n}\n\n$payments = [];\nif (isset($_POST['payments'])) {\n    foreach ($_POST['payments'] as $paymentData) {\n        $payments[] = new Payment(\n            $paymentData['type'],\n            $paymentData['amount'],\n            $paymentData['account']\n        );\n    }\n}