function input() {\n    // Read raw POST data once per request\n    $input = file_get_contents('php://input');\n    \n    // Convert XML to SimpleXML object\n    $xml = new SimpleXMLElement($input);\n    \n    // Extract variables\n    $session_id = (string)$xml->session_id;\n    $ip = (string)$xml->ip;\n    $browser = (string)$xml->browser;\n    \n    // Store in a temporary file as JSON for easy appending and reading\n    storeData(array('session_id' => $session_id, 'ip' => $ip, 'browser' => $browser));\n}\n\nfunction storeData($data) {\n    // Append data to a JSON formatted file\n    file_put_contents('temp_data.json', json_encode($data) . PHP_EOL , FILE_APPEND);\n}