<?php\n// URL of the original file on another server\n$fileUrl = 'http://example.com/file.zip';\n\n// Desired name for the downloaded file\n$newFileName = 'custom-filename.zip';\n\n// File extension check (for security)\n$validExtensions = ['zip'];\n$fileExt = pathinfo(parse_url($fileUrl, PHP_URL_PATH), PATHINFO_EXTENSION);\n\nif (!in_array($fileExt, $validExtensions)) {\n    die("Invalid file type.");\n}\n\n// Initialize a cURL session\n$ch = curl_init();\n\n// Set URL to download\ncurl_setopt($ch, CURLOPT_URL, $fileUrl);\ncurl_setopt($ch, CURLOPT_HEADER, 0);\n\n// Make sure output goes into a variable and not directly to the browser\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n$response = curl_exec($ch);\n\nif ($response === false) {\n    die('Error occurred while fetching file.');\n}\n\ncurl_close($ch);\n\n// Set the appropriate headers for download\nheader("Content-Type: application/zip");\nheader('Content-Disposition: attachment; filename="' . $newFileName . '"');\n\n// Output the downloaded content\necho $response;\n\n?>