class PageController extends Zend_Controller_Action {\n\n    public function indexAction() {\n        // Assume $this->contents is populated with your DB data\n        $this->_populateContainers();\n        \n        // Render the layout with containers pre-rendered\n        $this->render('layout');\n    }\n\n    private function _populateContainers() {\n        $leftContainer = '';\n        $centerContainer = '';\n        $rightContainer = '';\n\n        foreach ($this->contents as $item) {\n            ob_start();  // Start output buffering\n            \n            try {\n                $this->_helper->actionStack()->resetRequest();\n                $this->_forward($item['action'], $item['controller']);\n\n                if ($item['position'] == 'left') {\n                    $leftContainer .= ob_get_contents();\n                } elseif ($item['position'] == 'center') {\n                    $centerContainer .= ob_get_contents();\n                } elseif ($item['position'] == 'right') {\n                    $rightContainer .= ob_get_contents();\n                }\n                \n                ob_end_flush();  // Flush the contents\n            } catch (Exception $e) {\n                ob_end_clean();  // Clean buffer on error\n                \n                // Optionally handle or log the error here if needed\n            }\n        }\n\n        // Assign containers to view for layout rendering\n        $this->view->left_container = $leftContainer;\n        $this->view->center_container = $centerContainer;\n        $this->view->right_container = $rightContainer;\n    }\n}