function getPrimaryKey($tableName) {\n    $link = mysqli_connect("localhost", "username", "password", "database");\n    \n    if (!$link) {\n        die('Connect Error (' . mysqli_connect_errno() . ') ' . mysqli_connect_error());\n    }\n\n    $query = "SELECT COLUMN_NAME \n              FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE \n              WHERE TABLE_SCHEMA = '" . mysqli_real_escape_string($link, $dbName) . "' \n                AND TABLE_NAME = '" . mysqli_real_escape_string($link, $tableName) . "' \n                AND CONSTRAINT_NAME = 'PRIMARY'";\n\n    $result = mysqli_query($link, $query);\n    $primaryKey = null;\n\n    if ($result && mysqli_num_rows($result) > 0) {\n        $row = mysqli_fetch_row($result);\n        $primaryKey = $row[0];\n    }\n\n    mysqli_close($link);\n\n    return $primaryKey;\n}\n\n// Usage:\n$tableName = 'your_table_name';\n$dbName = 'your_database_name';\n$primaryKey = getPrimaryKey($tableName);\n\nif ($primaryKey) {\n    $sql = sprintf("UPDATE %s SET %s=%s WHERE %s = %s", \n                     mysqli_real_escape_string($link, $tableName), \n                     mysqli_real_escape_string($link, $columnToUpdate),\n                     mysqli_real_escape_string($link, $newValue),\n                     mysqli_real_escape_string($link, $primaryKey),\n                     mysqli_real_escape_string($link, $idValue));\n} else {\n    die('Primary key not found.');\n}