<?php\n// Use the Autoload file from the Composer dependencies\nrequire 'vendor/autoload.php';\n\nuse Twilio\Rest\Client;\n\n// Your Account SID and Auth Token from twilio.com/console\n$account_sid = 'your_account_sid';\n$auth_token = 'your_auth_token';\n$client = new Client($account_sid, $auth_token);\n\n// Use your account's phone number or a Twilio number as the from parameter\n$message = $client->messages->create(\n    // To number\n    "+1234567890",\n    [\n        "from" => "+0987654321", // From number (Twilio number)\n        "body" => "Hello, this is a message sent from PHP using Twilio!"\n    ]\n);\n\necho $message->sid;