function getFileCount($path = '.') {\n    $ignore = array('cgi-bin', '.', '..', '.DS_Store');\n    $filecount = 0;\n    \n    // Check if the path is a directory\n    if (is_dir($path)) {\n        $files = scandir($path);\n        \n        foreach ($files as $file) {\n            if (!in_array($file, $ignore)) {\n                $fullPath = "$path/$file";\n                \n                // If it's a directory, recurse into it\n                if (is_dir($fullPath)) {\n                    $filecount += getFileCount($fullPath);\n                } else {\n                    // If it's a file, increment the count\n                    $filecount++;\n                }\n            }\n        }\n    }\n    \n    return $filecount;\n}\n\n// Example usage:\necho getFileCount('./Dir_A'); // Should output 5 for your directory structure