<?php\n\nclass DynamicNavigation {\n    private $pdo;\n    private $database;\n\n    public function __construct($dsn, $username, $password, $database) {\n        $this->pdo = new PDO($dsn, $username, $password);\n        $this->database = $database;\n    }\n\n    public function generateNav() {\n        echo '<div class="dynamic_nav_head">Navigation</div>';\n        echo '<div class="dynamic_nav">';\n        \n        try {\n            $sql = "SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_SCHEMA = :database AND TABLE_NAME != 'includes'";\n            $stmt = $this->pdo->prepare($sql);\n            $stmt->execute(['database' => $this->database]);\n            \n            while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n                $table = $row['TABLE_NAME'];\n                echo "<div class='cat'>" . htmlspecialchars(ucwords($table)) . "</div>";\n                echo "<div class='cat_item'>";\n                echo "<a href='?page=read&section=" . urlencode($table) . "'>View " . htmlspecialchars(ucwords($table)) . "</a>";\n                echo "</div>";\n                echo "<div class='cat_item'>";\n                echo "<a href='?page=add&section=" . urlencode($table) . "'>Add New " . htmlspecialchars(ucwords($table)) . "</a>";\n                echo "</div>";\n            }\n        } catch (PDOException $e) {\n            error_log($e->getMessage());\n        }\n\n        echo '</div>';\n    }\n}\n\n// Usage\ninclude('data.php'); // Include your database configuration variables: $dsn, $username, $password, $database\n\n$navigation = new DynamicNavigation($dsn, $username, $password, $database);\n$navigation->generateNav();\n\n?>