<?php\n\nclass enc\n{\n    private $iv; // Initialization Vector\n\n    function __construct()\n    {\n        // Define a secure IV, it should be random but consistent for both encrypting and decrypting.\n        $this->iv = "fedcba9876543210"; // 16-byte IV\n    }\n\n    function encrypt($str, $key)\n    {\n        $key = $this->hex2bin($key);\n\n        $td = mcrypt_module_open("rijndael-128", "", "cbc", "");\n\n        mcrypt_generic_init($td, $key, $this->iv); // Use the defined IV\n        $encrypted = mcrypt_generic($td, $str);\n\n        mcrypt_generic_deinit($td);\n        mcrypt_module_close($td);\n\n        return bin2hex($encrypted);\n    }\n\n    function decrypt($code, $key)\n    {\n        $key = $this->hex2bin($key);\n        $code = $this->hex2bin($code);\n\n        $td = mcrypt_module_open("rijndael-128", "", "cbc", "");\n\n        mcrypt_generic_init($td, $key, $this->iv); // Use the defined IV\n        $decrypted = mdecrypt_generic($td, $code);\n\n        mcrypt_generic_deinit($td);\n        mcrypt_module_close($td);\n\n        return utf8_encode(trim($decrypted));\n    }\n\n    function hex2bin($hexdata)\n    {\n        $bindata = "";\n\n        for ($i = 0; $i < strlen($hexdata); $i += 2) {\n            $bindata .= chr(hexdec(substr($hexdata, $i, 2)));\n        }\n\n        return $bindata;\n    }\n}\n\n$theEncryption = new enc();\n$user = "John Doe";\n$email = "john@example.com";\n$user = $theEncryption->encrypt($user, "0123456789abcdef");\n\n$email = $theEncryption->encrypt($email, "0123456789abcdef");\n\necho 'User: '.$user.'<br>';\necho 'Email: '.$email;\n\n?>