<?php\n$box_query = mysql_query("SELECT * FROM `ta_boxs`");\necho '<form method="post" action="">';\nwhile ($box = mysql_fetch_array($box_query)) {\n    echo '<div class="box_content">';\n    echo '<div class="box">';\n    echo '<span class="box_top"></span>';\n    echo '<div class="box_header"><input type="text" name="title[' . $box['id'] . ']" class="title-input" value="' . htmlspecialchars($box['title']) . '" /></div>';\n    echo '<span class="sept"></span>';\n    echo '<div class="admin-back">';\n    echo '<p class="sub"><span>Includes:</span> Search for these terms in Twitter Feeds.</p>';\n    echo '<p class="sub-small">Please enter one word per field or click "add word".</p>';\n    foreach (['1', '2', '3'] as $i) {\n        echo '<p><input type="text" name="search_term_' . $i . '[' . $box['id'] . ']" value="' . htmlspecialchars($box['search_term_' . $i]) . '" class="term-input" /></p>';\n    }\n    echo '<span class="hr"></span>';\n    echo '<p class="sub"><span>Excludes:</span> Ignore these terms in Twitter Feeds.</p>';\n    echo '<p class="sub-small">Please enter one word per field or click "add word".</p>';\n    foreach (['1', '2', '3'] as $i) {\n        echo '<p><input type="text" name="exc_search_term_' . $i . '[' . $box['id'] . ']" value="' . htmlspecialchars($box['exc_search_term_' . $i]) . '" class="term-input" /></p>';\n    }\n    echo '</div>';\n    echo '</div>';\n    echo '<span class="box_bottom"></span>';\n    echo '</div>';  \n}\necho '<input type="submit" name="update" value="Update All" />';\necho '</form>';\n?>