$TempSQL = "SELECT field1, field2, field3 FROM table WHERE ";\n\n$params = [];\n$values = [];\n\nif ($numberParams == 1) {\n    $params[] = 'field1 = :param1';\n    $values[':param1'] = $val1;\n} else {\n    $params[] = 'field2 = :param2';\n    $params[] = 'field3 = :param3';\n    $values[':param2'] = $val2;\n    $values[':param3'] = $val3;\n}\n\n$TempSQL .= implode(' AND ', $params);\n\n$stmt = $db->prepare($TempSQL);\n$stmt->execute($values);\n\n// Fetch results\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);