$html = '<ct:form/input type="attr1" value="$item->field">';\npreg_match('/<ct:([^>\s]+)((?:\s+[a-zA-Z0-9:\-_]+\s*=\s*(?:"[^"]*"|\'[^\']*\'))*)>/im', $html, $matches);\n\n// Extracting tagname and attributes\n$tagName = $matches[1];\npreg_match_all('/([a-zA-Z0-9:\-_]+)\s*=\s*("(.*?)"|\'(.*?)\')/', $matches[2], $attributes, PREG_SET_ORDER);\n\necho "Tag Name: " . htmlspecialchars($tagName) . "\n";\nforeach ($attributes as $attribute) {\n    $name = $attribute[1];\n    // Extract the value from either index 3 (double quoted) or 4 (single quoted)\n    $value = !empty($attribute[3]) ? $attribute[3] : $attribute[4];\n    echo "Attribute: " . htmlspecialchars($name) . " Value: " . htmlspecialchars($value) . "\n";\n}