<?php\n// $fields should be an associative array of your fields and values\n$fields = [\n    'field1' => 'value1',\n    'field2' => 'value2',\n];\n\necho '<form id="paymentForm" action="https://secure-payment-gateway.com/" method="post">';\nforeach ($fields as $name => $value) {\n    echo '<input type="hidden" name="' . htmlspecialchars($name, ENT_QUOTES, 'UTF-8') . '" value="' . htmlspecialchars($value, ENT_QUOTES, 'UTF-8') . '">';\n}\necho '</form>';\necho '<script>document.getElementById("paymentForm").submit();</script>';\n?>