<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $name = $_POST['name'];\n    $email = $_POST['email'];\n\n    if (isset($_FILES['imagefile'])) {\n        $imageName = basename($_FILES['imagefile']['name']);\n        $imagePath = 'uploads/' . $imageName;\n\n        if (move_uploaded_file($_FILES['imagefile']['tmp_name'], $imagePath)) {\n            echo "The file {$imageName} has been uploaded successfully, along with name: {$name} and email: {$email}.";\n        } else {\n            echo "There was an error uploading the image.";\n        }\n    } else {\n        echo "No image received.";\n    }\n}\n?>