public function replace($Content)\n{\n    foreach ($this->results as $Element) {\n        // Clone the content to be inserted\n        $newNode = $Content->cloneNode(true);\n        \n        // Get the parent node of the current element\n        $parentNode = $Element->parentNode;\n        \n        // Insert the cloned node before the current element\n        $parentNode->insertBefore($newNode, $Element);\n        \n        // Remove the old element from its parent\n        $parentNode->removeChild($Element);\n    }\n\n    return $this;\n}