<?php\n   use PHPMailer\PHPMailer\PHPMailer;\n   use PHPMailer\PHPMailer\Exception;\n\n   require 'path/to/PHPMailer/src/Exception.php';\n   require 'path/to/PHPMailer/src/PHPMailer.php';\n   require 'path/to/PHPMailer/src/SMTP.php';\n\n   $mail = new PHPMailer(true);\n\n   try {\n       //Server settings\n       $mail->isSMTP();                                            // Send using SMTP\n       $mail->Host       = 'smtp.gmail.com';                     // Set the SMTP server to send through\n       $mail->SMTPAuth   = true;                                   // Enable SMTP authentication\n       $mail->Username   = 'your-email@gmail.com';               // SMTP username\n       $mail->Password   = 'your-app-password';                  // SMTP password (consider using an app-specific password)\n       $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;         // Enable TLS encryption; `PHPMailer::ENCRYPTION_SMTPS` also accepted\n       $mail->Port       = 587;                                    // TCP port to connect to\n\n       //Recipients\n       $mail->setFrom('your-email@gmail.com', 'Your Name');\n       $mail->addAddress('recipient-email@yahoo.com');           // Add a recipient\n\n       // Content\n       $mail->isHTML(false);                                        // Set email format to HTML\n       $mail->Subject = 'Form Submission Results';\n       $mail->Body    = "Name: xxx\nE-Mail: xxx@yahoo.com\nMessage: hey test message";\n\n       $mail->send();\n       echo 'Message has been sent';\n   } catch (Exception $e) {\n       echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";\n   }\n   ?>