<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    $choices = $_POST['choices'];\n    \n    if (count(array_unique($choices)) !== count($choices) || count($choices) < 5) {\n        echo "Error: Each choice must be unique and all choices must be selected.";\n        exit();\n    }\n\n    // Process the ranked preferences\n    foreach ($choices as $rank => $choice) {\n        $rank++; // Convert zero-based index to one-based rank\n        echo "$rank. Preferred Choice: $choice<br>";\n    \n        // Save these values in your MySQL database\n        // Example query: INSERT INTO user_preferences (userId, preferenceRank, choiceValue) VALUES (?, ?, ?)\n    }\n}\n?>