<?php\n// Open or create the SQLite database file\n$db = new PDO('sqlite:my_database.db');\n\n// Check if table exists, if not create it\n$query = "CREATE TABLE IF NOT EXISTS users (\n    id INTEGER PRIMARY KEY,\n    username TEXT NOT NULL,\n    password TEXT NOT NULL\n)";\n$db->exec($query);\n\n// Insert a record into the database\n$username = 'example_user';\n$passwordHashed = password_hash('secure_password', PASSWORD_BCRYPT); // Hashing the password\n$query = "INSERT INTO users (username, password) VALUES (:username, :password)";\n$stmt = $db->prepare($query);\n$stmt->execute([':username' => $username, ':password' => $passwordHashed]);\n\n// Query the database\n$query = "SELECT * FROM users";\n$result = $stmt->query($query);\n\nwhile ($row = $result->fetch(PDO::FETCH_ASSOC)) {\n    echo "ID: " . $row['id'] . ", Username: " . htmlspecialchars($row['username']) . "\n";\n}\n\n// Close the connection\n$db = null;\n?>