<?php\n    $MySQLPassword = "indian";  \n    $HostName = "localhost";    \n    $UserName = "monty";\n\n    // Establish a connection to MySQL (use mysqli or PDO for better security and functionality)\n    mysql_connect($HostName, $UserName, $MySQLPassword) \n        or die("ERROR: Could not connect to database!");\n\n    mysql_select_db("sachin") or die("cannot select db");\n\n    // Sanitize input to prevent SQL injection\n    $keyword = mysql_real_escape_string($_POST['keyword']);\n    echo $keyword;\n\n    /* Execute the query that performs the actual search in the DB */\n    $result = mysql_query("\n        SELECT p.page_url AS url,\n               COUNT(*) AS occurrences \n        FROM page p, word w, occurrence o\n        WHERE p.page_id = o.page_id AND\n              w.word_id = o.word_id AND\n              w.word_word = \"$keyword\"\n        GROUP BY p.page_id\n        ORDER BY occurrences DESC\n    ");\n\n    $output = new DOMDocument();\n    $output->formatOutput = true;\n    \n    $root = $output->createElement("loginsuccess");\n    $output->appendChild($root);\n\n    while ($row = mysql_fetch_array($result)) {  \n        $personNode = $output->createElement("person");\n\n        $keywordNode = $output->createElement("keyword", htmlspecialchars($keyword));\n        $personNode->appendChild($keywordNode);\n\n        $nameNode = $output->createElement("name", htmlspecialchars($row['url']));\n        $personNode->appendChild($nameNode);\n\n        $occurNode = $output->createElement("occur", htmlspecialchars($row['occurrences']));\n        $personNode->appendChild($occurNode);\n\n        $root->appendChild($personNode);\n    }\n\n    // Save the XML to a file, overwriting any existing content\n    file_put_contents('output.xml', $output->saveXML(), LOCK_EX);\n\n    mysql_close();\n?>