function dateTimeFormat($dateIn) {\n    $x = explode(" ", $dateIn);\n    $y = explode("-", $x[0]);\n    $z = explode(":", $x[1]);\n\n    // Explicitly cast to integers\n    $year = (int)$y[0];\n    $month = (int)$y[1];\n    $day = (int)$y[2];\n    $hour = (int)$z[0];\n    $min = (int)$z[1];\n    $sec = 0; // Assuming seconds are not provided, default to 0\n\n    // Use DateTime for better handling and timezones\n    $dateTime = new DateTime(sprintf('%d-%d-%d %d:%d:%d', $year, $month, $day, $hour, $min, $sec));\n    \n    // Format the date and time\n    $dateOut = $dateTime->format("F j, Y h:i A");\n\n    return $dateOut;\n}