function performSearch($index, $term) {\n    // Normalize query by removing spaces for alternative searches\n    $normalizedTerm = str_replace(' ', '', $term);\n\n    // Create queries for both terms\n    $query1 = Zend_Search_Lucene_QueryParser::parse('"' . $term . '"');\n    $query2 = Zend_Search_Lucene_QueryParser::parse($normalizedTerm);\n\n    // Combine queries into a hit collector to gather all potential matches\n    $hitsCollector = new Zend_Search_Lucene_Search_Hits();\n    \n    foreach ($index->find($query1) as $hit) {\n        $hitsCollector->addHit($hit);\n    }\n    foreach ($index->find($query2) as $hit) {\n        if (!$hitsCollector->hasHit($hit->id)) { // Avoid duplicates\n            $hitsCollector->addHit($hit);\n        }\n    }\n\n    return $hitsCollector;\n}\n\n// Usage:\n$index = Zend_Search_Lucene::open('/path/to/index/');\n$results = performSearch($index, 'Foo Bar');