// Create a new mysqli connection\n$conn = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare and bind\n$stmt = $conn->prepare("INSERT INTO `table` (`column`) VALUES (?)");\n$stmt->bind_param("s", $safe_variable);\n\n// Set parameters and execute\n$unsafe_variable = $_POST['user_input'];\n$safe_variable = mysqli_real_escape_string($conn, $unsafe_variable); // Although not necessary with prepared statements, this shows data handling\n\nif ($stmt->execute()) {\n    echo "New record created successfully";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n// Close connections\n$stmt->close();\n$conn->close();