<?php\nrequire_once __DIR__ . '/vendor/autoload.php';\n\n$mpdf = new \Mpdf\Mpdf();\n\n$data = [\n    ['Item', 'Quantity', 'Price'],\n    ['Widget A', 2, 15.99],\n    ['Gadget B', 5, 7.49]\n];\n\n$html = '<table border="1">';\nforeach ($data as $row) {\n    $html .= '<tr>';\n    foreach ($row as $cell) {\n        $html .= "<td>{$cell}</td>";\n    }\n    $html .= '</tr>';\n}\n$html .= '</table>';\n\n$mpdf->WriteHTML($html);\n$mpdf->Output('example.pdf', \Mpdf\Output\Destination::DOWNLOAD);\n?>