// Initialize your cURL session if not already done\n$curl_handle = curl_init();\n\n// Set the URL for your request\ncurl_setopt($curl_handle, CURLOPT_URL, 'http://example.com');\n\n// Reset all POST related options\ncurl_setopt($curl_handle, CURLOPT_POSTFIELDS, null); // Clear post fields\ncurl_setopt($curl_handle, CURLOPT_POST, false);       // Indicate we're not doing a POST\n\n// Explicitly set the request method to GET (though this is usually default)\ncurl_setopt($curl_handle, CURLOPT_HTTPGET, true);\n\n// Ensure that other settings are appropriate for a GET request\ncurl_setopt($curl_handle, CURLOPT_HEADER, false);    // Do not include header in output\ncurl_setopt($curl_handle, CURLOPT_RETURNTRANSFER, true);// Return the response as a string\n\n// Execute your cURL request\n$response = curl_exec($curl_handle);\n\n// Check for errors\nif ($response === false) {\n    echo 'cURL Error: ' . curl_error($curl_handle);\n}\n\n// Close the cURL session\ncurl_close($curl_handle);