<?php\n\n$xml = <<<'XML'\n<root>\n    <element media="  screen  ,  print  ">Content</element>\n</root>\nXML;\n\n$dom = new DOMDocument();\n$dom->loadXML($xml);\n\n$xpath = new DOMXPath($dom);\n$query = "./normalize-space(@media)";\n\n// When using normalize-space, you need to select the node and then apply the function\n$result = $xpath->evaluate("string(//element/@media/normalize-space())");\n// Alternatively, directly use it in a way that works with XPath:\n$nodes = $xpath->query("//element");\n\nforeach ($nodes as $node) {\n    // Use PHP's trim and explode to achieve similar effect if necessary\n    $media = $xpath->evaluate("normalize-space($node/@media)");\n    echo $media;  // Outputs "screen,print"\n}\n\n?>