public function RPCRequest($method, $params) {\n    $request = xmlrpc_encode_request($method, $params);\n    $context = stream_context_create(array('http' => array(\n                  'method' => "POST",\n                  'header' => "Content-Type: text/xml",\n                  'content' => $request\n    )));\n\n    ob_start(); // Start output buffering to capture errors\n    $file = @file_get_contents($this->url, false, $context); // Suppress errors temporarily\n    $errors = ob_end_clean(); // Capture and clean any output (errors)\n\n    if ($file === FALSE) {\n        throw new Exception("Failed to fetch data. Errors: " . $errors);\n    }\n    \n    if(trim($file) === '') { \n        return array(); // Return empty array for no content\n    }\n\n    $decoded = @xmlrpc_decode($file); // Suppress errors temporarily\n    if (is_null($decoded)) {\n        throw new Exception("Failed to decode XML. Response: " . htmlspecialchars($file));\n    }\n    \n    return $decoded;\n}