$str = '<a href="1.html" href="2.html">link</a>';\n$doc = new DOMDocument();\n@$doc->loadHTML($str);\n$xpath = new DOMXPath($doc);\n\n// Get the last href attribute value by first removing duplicates or handling as string\n$links = $xpath->query("//a");\nforeach ($links as $link) {\n    // Assuming the HTML is malformed and we need to extract the last occurrence of 'href'\n    if (preg_match_all('/href="([^"]+)"/', $doc->saveHTML($link), $matches)) {\n        $lastHref = end($matches[1]);\n        echo $lastHref;  // Outputs: "2.html"\n    }\n}