<?php\n\nfunction isProxy() {\n    $proxy = false;\n\n    // Check for specific HTTP headers often used by proxies\n    if (\n        !empty($_SERVER['HTTP_VIA']) ||\n        isset($_SERVER['HTTP_X_FORWARDED_FOR']) ||\n        isset($_SERVER['HTTP_FORWARDED_FOR']) ||\n        isset($_SERVER['HTTP_X_FORWARDED']) ||\n        isset($_SERVER['HTTP_FORWARDED']) ||\n        isset($_SERVER['HTTP_X_HTTPS_FROM']) ||\n        isset($_SERVER['HTTP_VIA'])\n    ) {\n        return true;\n    }\n\n    // Check for known proxy IP ranges (this is a very basic example)\n    $ip = $_SERVER['REMOTE_ADDR'];\n    $knownProxies = [\n        '192.168.',\n        '10.',       // RFC 1918 private IPs\n        '172.16.',\n        '127.0.0.',   // Localhost\n        // Add known ranges here...\n    ];\n\n    foreach ($knownProxies as $range) {\n        if (strpos($ip, $range) === 0) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\nif (isProxy()) {\n    echo "Proxy detected!";\n} else {\n    echo "No proxy detected.";\n}\n?>