function validatePassword($password) {\n    // Split the string into an array of characters\n    $chars = str_split($password);\n\n    // Use preg_match to check for at least one uppercase letter\n    $hasCapital = preg_match('/[A-Z]/', $password);\n\n    // Use preg_match to check for at least one symbol or number\n    $hasSymbolOrNumber = preg_match('/[\W\d]/', $password);\n\n    // Return true if both conditions are met\n    return $hasCapital && $hasSymbolOrNumber;\n}\n\n$password = "YourPassword1!";\nif (validatePassword($password)) {\n    echo "Password is valid.";\n} else {\n    echo "Password is invalid. It must contain at least one capital letter and one symbol or number.";\n}