function exportNamesCommentsCSV($table, $columns) {\n    $file = "volunteer_comments";\n    $csv_output = '';\n    \n    // Fetch column headers\n    $result = mysql_query("SELECT * FROM ".$table." LIMIT 1");\n    if (mysql_num_rows($result) > 0) {\n        $row = mysql_fetch_assoc($result);\n        $columns = array_keys($row);\n        $csv_output .= implode(", ", $columns) . "\n";\n    }\n\n    // Fetch data\n    $values = mysql_query("SELECT lname, fname, email, comments FROM ".$table."");\n    while ($rowr = mysql_fetch_assoc($values)) {\n        foreach ($rowr as $k => &$v) {\n            // Enclose field in double quotes and escape existing double quotes\n            if (strpos($v, ',') !== false || strpos($v, '"') !== false || strpos($v, "\n") !== false) {\n                $v = '"' . str_replace('"', '""', $v) . '"';\n            }\n        }\n        unset($v); // Unset reference to last item\n        $csv_output .= implode(", ", $rowr) . "\n";\n    }\n\n    $filename = $file."_".date("Y-m-d_H-i", time());\n    header("Content-type: application/csv");\n    header("Content-disposition: attachment; filename=".$filename.".csv");\n    print $csv_output;\n    exit;\n}