function substr_word($string, $length) {\n    if (strlen($string) <= $length) {\n        return $string;\n    }\n    \n    // Find the position of the last space within the limit\n    $limitedString = substr($string, 0, $length);\n    preg_match('/^\s*.{1,' . ($length - 3) . '}(\s|$)/', $string, $matches);\n    \n    return trim(end($matches)) . '...';\n}\n\n$body = "Your text goes here and it should not be cut in the middle of a word.";\necho substr_word($body, 260);