<?php\n$myVariable = "Some text";\n?>\n\n<!-- Using GET method -->\n<form action="Page2.php" method="get">\n    <input type="hidden" name="filename" value="<?php echo htmlspecialchars($myVariable); ?>">\n    <button type="submit">Submit</button>\n</form>\n\n<!-- Alternatively, directly in URL (be cautious with long strings or special characters) -->\n<a href="Page2.php?filename=<?php echo urlencode($myVariable); ?>">Go to Page 2</a>