<?php\nheader("Content-type: text/css");\n// Assuming $db_connection is your PDO connection and $colors is the result of a query.\n$color_query = $db_connection->prepare("SELECT color FROM settings WHERE setting_name = :setting");\n$color_query->execute(['setting' => 'primary_color']);\n$primary_color = htmlspecialchars($color_query->fetchColumn(), ENT_QUOTES, 'UTF-8');\n?>\nbody {\n    background-color: <?php echo $primary_color; ?>;\n}