// Example structure of a function using simple probabilities\n   function classifySpam($text, $spamNgramsProbabilities, $hamNgramsProbabilities) {\n       $ngrams = getWordNGrams($text);\n       \n       $spamProbability = 1.0;\n       $hamProbability = 1.0;\n       \n       foreach ($ngrams as $ngram) {\n           if (isset($spamNgramsProbabilities[$ngram])) {\n               $spamProbability *= $spamNgramsProbabilities[$ngram];\n           }\n           \n           if (isset($hamNgramsProbabilities[$ngram])) {\n               $hamProbability *= $hamNgramsProbabilities[$ngram];\n           }\n       }\n       \n       return $spamProbability > $hamProbability ? 'Spam' : 'Ham';\n   }