function getValue($cn, $comando) {\n    $resul = $cn->query($comando);\n    if (!$resul) return null;\n    \n    // Fetch all results from the stored procedure call\n    while ($res = $resul->fetch(PDO::FETCH_NUM)) {\n        $retorno = $res[0];\n        break; // Assuming you only need the first returned value\n    }\n    \n    // Ensure all result sets are handled\n    while ($cn->nextRowset()) {\n        // Optionally process additional result sets here if needed.\n    }\n\n    return $retorno;\n}