<?php\n$biggest = 0;\n$startNumber = 1;\n\nfunction test($i, $original) {\n    global $biggest, $startNumber;\n    static $counter = 0; // Use a static variable to keep track of steps within each call chain\n\n    if ($i == 1) {\n        if ($counter > $biggest) {\n            $biggest = $counter;\n            $startNumber = $original;\n        }\n        echo "I'm done! Original number was $original, took $counter steps\n";\n        $counter = 0; // Reset counter for the next sequence\n    } else {\n        if ($i % 2 == 0) {\n            $i /= 2;\n        } else {\n            $i = 3 * $i + 1;\n        }\n        $counter++;\n        test($i, $original);\n    }\n}\n\nfor ($i = 1; $i < 1000000; $i++) {\n    echo "Testing number: $i\n";\n    test($i, $i);\n}\n\necho "The starting number that produces the most steps is $startNumber with $biggest steps.\n";\n?>